import Validate from '../Validate';
import SPEventManager from './SPEventManager';
var SPEvent = (function () {
    function SPEvent(name) {
        Validate.isNonemptyString(name, 'name');
        this._name = name;
    }
    SPEvent.prototype.add = function (observer, eventHandler) {
        Validate.isNotNullOrUndefined(observer, 'component');
        Validate.isNotNullOrUndefined(eventHandler, 'eventHandler');
        SPEventManager.instance.registerEvent(this._name, observer, eventHandler);
    };
    SPEvent.prototype.remove = function (observer, eventHandler) {
        Validate.isNotNullOrUndefined(observer, 'component');
        Validate.isNotNullOrUndefined(eventHandler, 'eventHandler');
        SPEventManager.instance.unregisterEvent(this._name, observer, eventHandler);
    };
    return SPEvent;
}());
export default SPEvent;
