import { SPEvent } from '@microsoft/sp-core-library';
import { IAadTokenProvider, IAadTokenProviderConfiguration } from './IAadTokenProvider';
import { TokenAcquisitionEventArgs } from './AadTokenProvider';
/**
 * This class is an implementation of the AadTokenProvider in environments that AAD cannot be reached.
 *
 * @internal
 * @sealed
 */
export default class MockAadTokenProvider implements IAadTokenProvider {
    /**
     * Token Acquisition Event String
     */
    static _tokenAcquisitionEventId: string;
    private _tokenAcquisitionEvent;
    constructor();
    /**
     * Returns a rejected promise for retrieving an AAD OAuth2 token.
     *
     * @param resourceEndpoint - the resource for which the token should be obtained
     * @returns A promise that will be fullfiled with the token or that will reject
     *          with an error message
     */
    getToken(resourceEndpoint: string, configuration?: IAadTokenProviderConfiguration): Promise<string>;
    /**
     * Notifies the developer when Token Acquistion requires user action.
     * @eventproperty
     * @beta
     */
    readonly tokenAcquisitionEvent: SPEvent<TokenAcquisitionEventArgs>;
}
