import { DynamicDataProvider } from '@microsoft/sp-component-base';
/**
 * Dynamic data internal configuration.
 *
 * @remarks
 * This contract represents all the required dynamic data related configuration
 * for the component. This data is essential in creating the dynamic properties.
 * This is filled by the framework. For example in BaseWebPart class for web parts.
 *
 * @internal
 */
export interface IDynamicConfiguration {
    /**
     * The Dynamic Data provider for the component.
     *
     * @remarks
     * There is only one 'dynamicDataProvider' for a component
     */
    dynamicDataProvider: DynamicDataProvider;
    /**
     * Default callback.
     *
     * @remarks
     * If 'callback' is not supplied for any of the dynamic properties, for both
     * field and property pane level, then this is used as the default.
     *
     * This is a function that wraps the render() function.
     * This maps with the behavior from the deserialization of DD objects.
     */
    defaultCallback: () => void;
}
