/// <reference types="react" />
import * as React from 'react';
import { IDynamicDataWidgetProps, IDynamicDataWidgetState } from './IDynamicDataWidget';
export default class DynamicDataWidget extends React.Component<IDynamicDataWidgetProps, IDynamicDataWidgetState> {
    private _sources;
    private _selectedSourceText;
    constructor(props: IDynamicDataWidgetProps);
    componentWillReceiveProps(): void;
    render(): React.ReactElement<HTMLDivElement>;
    /**
     * Refreshes the avaialble sources.
     */
    private _refreshAvailableSources();
    /**
     * When the source is changed, we set the selected source and reset the rest of the state.
     * @param option
     */
    private _onSourceChange(option);
    /**
     * Returns the required dropdowns.
     */
    private _getEntryWidgets();
    /**
     * Retuns data source dropdown options with id-title pairs of all DD sources that are currently available.
     */
    private _getDataSourcesDropdownOptions(selectedSourceId);
    /**
     * Constructs the state of the component.
     */
    private _constructState();
    /**
     * Returns the source if already selected, else empty string.
     */
    private _getSelectedSourceId();
}
