import { DynamicProperty, DynamicDataProvider } from '@microsoft/sp-component-base';
/**
 * @internal
 */
export interface IDynamicDataWidgetProps {
    /**
     * A unique key of the property pane group field, in which the widget is rendered.
     */
    parentKey: string;
    /**
     * The Dynamic Data provider for the component.
     */
    dynamicDataProvider: DynamicDataProvider;
    /**
     * Entries on the widget.
     */
    entries: IDynamicDataWidgetEntry[];
    /**
     * Callback when the dynamic data changes are applied on the widget.
     */
    onApplyClick: (targetProperty: string, dynamicProperty: DynamicProperty<{}>) => void;
    /**
     * Label for the dynamic widget.
     */
    label?: string;
}
/**
 * @internal
 */
export interface IDynamicDataWidgetState {
    /**
     * Selected source in the data source dropdown
     */
    selectedSourceId: string;
    /**
     * Display text of the selected source.
     */
    selectedSourceText?: string;
}
/**
 * @internal
 */
export interface IDynamicDataWidgetEntry {
    /**
     * Target property associated with the entry.
     */
    targetProperty: string;
    /**
     * Value of the target property.
     */
    value: DynamicProperty<any>;
    /**
     * Label for the entry.
     */
    label?: string;
}
