import { DynamicProperty, DynamicDataProvider } from '@microsoft/sp-component-base';
/**
 * @internal
 */
export interface IDynamicDataWidgetEntryProps {
    /**
     * A unique key of the property pane group field, in which the widget is rendered.
     */
    parentKey: string;
    /**
     * Display text of the selected source.
     */
    selectedSourceText: string;
    /**
     * Selected data source for the entry.
     */
    selectedSourceId: string;
    /**
     * Target property associated with the entry.
     */
    targetProperty: string;
    /**
     * Value of the target property.
     */
    value: DynamicProperty<any>;
    /**
     * Label for the entry.
     */
    label?: string;
    /**
     * Dynamic data provoder of the component.
     */
    dynamicDataProvider: DynamicDataProvider;
    /**
     * Callback when the dynamic data changes are applied on the widget entry.
     */
    onApplyClick: (targetProperty: string, dynamicProperty: DynamicProperty<{}>) => void;
    /**
     * Indicates whether the borders should be shown or not.
     */
    showBorders: boolean;
}
/**
 * @internal
 */
export interface IDynamicDataWidgetEntryState {
    isApplyButtonEnabled: boolean;
    selectedPropertyId?: string;
    selectedPropertyText?: string;
    selectedSubPropertyText?: string;
    selectedPropertyPath?: string;
}
